SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rop_gerar_movto_ressuprimento ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_local_w		bigint;
nr_seq_roupa_w		bigint;
qt_maximo_w		double precision;
qt_saldo_w		double precision;
cd_estabelecimento_w	bigint;
qt_reposicao_w		double precision;

C01 CURSOR FOR
SELECT	nr_seq_roupa,
	qt_maximo,
	rop_obter_saldo_roupa(nr_seq_roupa, nr_seq_local_w, trunc(clock_timestamp(),'mm'))
from	rop_local_padrao_ressup
where	nr_seq_local = nr_seq_local_w;



BEGIN

delete from rop_movto_roupa
where nr_sequencia = nr_sequencia_p
and ie_ressuprimento = 'S';

select	coalesce(max(nr_seq_local_orig_dest),0),
	coalesce(max(cd_estabelecimento),0)
into STRICT	nr_seq_local_w,
	cd_estabelecimento_w
from	rop_lote_movto
where nr_sequencia = nr_sequencia_p;

if (nr_seq_local_w > 0) then

	open C01;
	loop
	fetch C01 into
		nr_seq_roupa_w,
		qt_maximo_w,
		qt_saldo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (qt_saldo_w < qt_maximo_w) then

			qt_reposicao_w	:= 0;
			qt_reposicao_w	:= qt_maximo_w - qt_saldo_w;


			if (qt_reposicao_w > 0) then
				insert into rop_movto_roupa(
					nr_sequencia,
					nr_seq_lote,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_roupa,
					dt_origem,
					cd_estabelecimento,
					ie_oper_correta,
					qt_roupa,
					ie_ressuprimento)
				values (	nextval('rop_movto_roupa_seq'),
					nr_sequencia_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_roupa_w,
					trunc(clock_timestamp(),'dd'),
					cd_estabelecimento_w,
					'N',
					qt_reposicao_w,
					'S');
			end if;
		end if;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
