SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rop_gerar_roupa ( nr_sequencia_p bigint, cd_barras_p text, cd_empresa_p bigint, nm_usuario_p text, ds_erro INOUT text) AS $body$
DECLARE


cd_material_w		bigint;
qt_existe_w		bigint;
ds_erro_w		varchar(255):= '';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	rop_roupa
where	cd_barra_roupa = cd_barras_p;

if (qt_existe_w > 0) then
	ds_erro_w	:= substr(ds_erro_w || wheb_mensagem_pck.get_texto(1163487) || cd_barras_p || chr(13) || chr(10),1,255);
end if;

if (qt_existe_w = 0) then
	insert into rop_roupa(
		nr_sequencia,
		nr_seq_lote_roupa,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		cd_barra_roupa,
		cd_empresa)
	Values (	nextval('rop_roupa_seq'),
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		'A',
		cd_barras_p,
		cd_empresa_p);
end if;

commit;

ds_erro	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
