CREATE OR REPLACE PROCEDURE rop_gerar_setores_inv_ajuste ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w	integer;
cd_pessoa_resp_w		varchar(10);

c01 CURSOR FOR
SELECT distinct cd_setor_atendimento
from (
	SELECT	a.cd_setor_atendimento
	from	rop_regra_roupa_setor a
	where	a.cd_estabelecimento = cd_estabelecimento_p
	and not exists (
		select	1
		from	rop_inv_ajuste_setor x
		where	x.nr_seq_inventario = nr_sequencia_p
		and	a.cd_setor_atendimento = x.cd_setor_atendimento)
	
union

	select	b.cd_setor_atendimento
	from	rop_regra_roupa_setor a,
		rop_regra_roupa_setores b
	where	a.nr_sequencia = b.nr_seq_regra
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and not exists (
		select	1
		from	rop_inv_ajuste_setor x
		where	x.nr_seq_inventario = nr_sequencia_p
		and	a.cd_setor_atendimento = x.cd_setor_atendimento)) alias2;


BEGIN

open C01;
loop
fetch C01 into
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	cd_pessoa_resp
	into STRICT	cd_pessoa_resp_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_w;

	insert into rop_inv_ajuste_setor(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_inventario,
		cd_setor_atendimento,
		cd_resp_contagem)
	values (	nextval('rop_inv_ajuste_setor_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		cd_setor_atendimento_w,
		cd_pessoa_resp_w);
	end;
end loop;
close C01;

commit;

end;
$body$
