CREATE OR REPLACE PROCEDURE rop_gerar_todos_setores_lav ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w	integer;
cd_pessoa_resp_w		setor_atendimento.cd_pessoa_resp%type;
IE_SITUACAO_w			varchar(1);
DS_OBSERVACAO_w         varchar(2000);
QT_OCUPACAO_SETOR_w		double precision;

c01 CURSOR FOR
SELECT distinct cd_setor_atendimento
from (
	SELECT	a.cd_setor_atendimento
	from	ROP_LAVANDERIA_SETOR a
	where	a.cd_estabelecimento = cd_estabelecimento_p
	and not exists (
		select	1
		from	ROP_LAVANDERIA_SETORES x
		where	x.NR_SEQ_LAVANDERIA = nr_sequencia_p
		and	a.cd_setor_atendimento = x.cd_setor_atendimento)
	
union

	select	a.cd_setor_atendimento
	from	ROP_LAVANDERIA_SETOR a
	where	a.cd_estabelecimento = cd_estabelecimento_p
	and not exists (
		select	1
		from	ROP_LAVANDERIA_SETORES x
		where	x.NR_SEQ_LAVANDERIA = nr_sequencia_p
		and	a.cd_setor_atendimento = x.cd_setor_atendimento)) alias2;


BEGIN

open C01;
loop
fetch C01 into
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	cd_pessoa_resp
	into STRICT	cd_pessoa_resp_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_w;
	insert into ROP_LAVANDERIA_SETORES(
		NR_SEQUENCIA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_SEQ_LAVANDERIA,
		CD_SETOR_ATENDIMENTO,
		QT_PESO,
		DS_OBSERVACAO,
		QT_OCUPACAO_SETOR)
	values (nextval('rop_lavanderia_setores_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		cd_setor_atendimento_w,
		0,
		DS_OBSERVACAO_w,
		QT_OCUPACAO_SETOR_w);
		end;
end loop;
close C01;

commit;

end;
