CREATE OR REPLACE PROCEDURE rop_gera_peca_setor_inv_ajuste ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w		bigint;
cd_estabelecimento_w		bigint;
nr_seq_roupa_w			bigint;
nr_seq_kit_w			bigint;
nr_sequencia_w			bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia,
	b.cd_setor_atendimento,
	a.cd_estabelecimento
from	rop_inventario_ajuste a,
	rop_inv_ajuste_setor b
where	a.nr_sequencia = b.nr_seq_inventario
and	a.nr_sequencia = nr_sequencia_p;

c02 CURSOR FOR
SELECT	distinct
	nr_seq_roupa,
	nr_seq_kit
from (	select	a.nr_seq_roupa,
		a.nr_seq_kit
	from	rop_regra_roupa_setor a
	where	a.cd_estabelecimento = cd_estabelecimento_w
	and	a.cd_setor_atendimento = cd_setor_atendimento_w
	
union

	SELECT	a.nr_seq_roupa,
		a.nr_seq_kit
	from	rop_regra_roupa_setor a,
		rop_regra_roupa_setores b
	where	a.nr_sequencia = b.nr_seq_regra
	and	a.cd_estabelecimento = cd_estabelecimento_w
	and	b.cd_setor_atendimento = cd_setor_atendimento_w
	and	b.cd_setor_atendimento <> a.cd_setor_atendimento) alias0;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_setor_atendimento_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_roupa_w,
		nr_seq_kit_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		insert into rop_inv_ajuste_roupa(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_inventario,
			nr_seq_inv_setor,
			nr_seq_roupa,
			qt_contagem,
			nr_seq_kit)
		values (	nextval('rop_inv_ajuste_roupa_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_p,
			nr_sequencia_w,
			nr_seq_roupa_w,
			0,
			nr_seq_kit_w);

		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
