CREATE OR REPLACE PROCEDURE rop_grava_pecas_lavanderia ( nr_sequencia_p bigint, cd_barra_roupa_p text, ie_gera_peso_p text, qt_roupa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_roupa_w		bigint;
qt_peso_w		double precision;
qt_roupa_w		double precision;
ie_grava_quantidade_w	varchar(1);
cd_perfil_w		bigint;
cd_estabelecimento_w	bigint;
nr_seq_rop_lav_item_w	rop_lavanderia_item.nr_sequencia%type;
vl_preco_w		rop_lavanderia_item.vl_unitario%type;


BEGIN

qt_roupa_w 		:= qt_roupa_p;
cd_perfil_w		:= obter_perfil_ativo;

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	rop_lavanderia
where	nr_sequencia = nr_sequencia_p;

ie_grava_quantidade_w	:= coalesce((obter_valor_param_usuario(1301, 95, cd_perfil_w, nm_usuario_p, cd_estabelecimento_w)), 'N');

if (ie_grava_quantidade_w = 'N') then
	qt_roupa_w := null;
end if;

select	a.nr_sequencia
into STRICT	nr_seq_roupa_w
from	rop_roupa a
where	a.cd_barra_roupa = cd_barra_roupa_p;

if (ie_gera_peso_p = 'S') then
	select	rop_obter_peso_roupa(nr_seq_roupa_w)
	into STRICT	qt_peso_w
	;
end if;

select	obter_vl_unit_rop_preco(nr_seq_roupa_w, clock_timestamp())
into STRICT	vl_preco_w
;

select	nextval('rop_lavanderia_item_seq')
into STRICT	nr_seq_rop_lav_item_w
;

insert into rop_lavanderia_item(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_lavanderia,
	nr_seq_roupa,
	qt_peso,
	qt_roupa,
	vl_unitario)
values (nr_seq_rop_lav_item_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_sequencia_p,
	nr_seq_roupa_w,
	qt_peso_w,
	qt_roupa_w,
	vl_preco_w);
commit;

end;
$body$
