SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rop_liberar_inv_ajuste ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_existe_w		bigint;

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	rop_inv_ajuste_setor
where	nr_seq_inventario = nr_sequencia_p;

if (qt_existe_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265547);
	--'Não é possível liberar o inventário sem cadastrar nenhum setor para o mesmo.'
end if;

update	rop_inventario_ajuste
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
