CREATE OR REPLACE PROCEDURE rop_liberar_inv_reposicao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	rop_inv_reposicao_item
where	nr_seq_inventario = nr_sequencia_p;

if (qt_existe_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265559);
	--'Este inventário não possui nenhum item.'
end if;

update	rop_inv_reposicao
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p
where	nr_Sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
