CREATE OR REPLACE PROCEDURE rothman_envio_hl7 ( nr_sequencia_p bigint, nr_atendimento_p bigint, nm_usuario_p text, ie_opcao_p text default null) AS $body$
DECLARE

ds_sep_bv_w				varchar(100);
ds_param_integ_hl7_w			varchar(4000);
nr_seq_atepacu_w			bigint;
cd_pessoa_fisica_w			varchar(10);

BEGIN
ds_sep_bv_w := obter_separador_bv;
 
 
 
nr_seq_atepacu_w	:= obter_atepacu_paciente(nr_atendimento_p, 'A');
 
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where 	nr_atendimento	= nr_atendimento_p;
 
 
ds_param_integ_hl7_w := 'nr_atendimento=' || nr_atendimento_p || ds_sep_bv_w || 
			'nr_seq_interno=' || nr_seq_atepacu_w || ds_sep_bv_w|| 
			'cd_pessoa_fisica=' || cd_pessoa_fisica_w || ds_sep_bv_w|| 
			'nr_seq_escala=' || nr_sequencia_p || ds_sep_bv_w;
 
			 
/* Sinaiv Vitais*/
			 
if (coalesce(ie_opcao_p::text, '') = '') or (ie_opcao_p = 'SV') then 
	CALL gravar_agend_integracao(238, ds_param_integ_hl7_w);
end if;
 
/* Exames laboratorias*/
	 
 
if (coalesce(ie_opcao_p::text, '') = '') or (ie_opcao_p = 'EX') then		 
	CALL gravar_agend_integracao(240, ds_param_integ_hl7_w);
end if;
 
/* Nursing Assessments*/
	 
 
if (coalesce(ie_opcao_p::text, '') = '') or (ie_opcao_p = 'SAE') or (ie_opcao_p = 'MOR') or (ie_opcao_p = 'BRA') then		 
	CALL gravar_agend_integracao(241, ds_param_integ_hl7_w);
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
