SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rp_altera_status_lista (nr_sequencia_p bigint, ie_status_p text) AS $body$
DECLARE


ie_liberadas_w	varchar(1);


BEGIN

ie_liberadas_w := OBTER_VALOR_PARAM_USUARIO(9091,38,Obter_perfil_Ativo,wheb_usuario_pck.get_nm_usuario,1);

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_sequencia_p > 0) then
	update 	rp_lista_espera_modelo
	set		ie_status = ie_status_p
	where	nr_sequencia = nr_sequencia_p;
end if;

if (ie_status_p = 'T') and (ie_liberadas_w = 'S') then
	update 	rp_lista_espera_modelo
	set	dt_liberacao_medico = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
