CREATE OR REPLACE PROCEDURE rp_atualiza_observacao_agenda ( cd_pessoa_fisica_p text, dt_inicial_p timestamp, dt_final_p timestamp, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

			 
nr_sequencia_w	bigint;			
 
C01 CURSOR FOR 
	SELECT nr_sequencia 
	from agenda_consulta 
	where 1 = 1 
	and cd_pessoa_fisica = cd_pessoa_fisica_p 
	and DT_AGENDA between trunc(dt_inicial_p) and trunc(dt_final_p) + 86399/86400 
	and	coalesce(ds_observacao::text, '') = '' 
	and	((NR_SEQ_RP_ITEM_IND IS NOT NULL AND NR_SEQ_RP_ITEM_IND::text <> '') or (NR_SEQ_RP_MOD_ITEM IS NOT NULL AND NR_SEQ_RP_MOD_ITEM::text <> '')) 
	and		obter_estab_agenda(cd_agenda) = wheb_usuario_pck.get_cd_estabelecimento;
	

BEGIN 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then 
	 
	update 	agenda_consulta 
	set 	ds_observacao = ds_observacao_p, 
			nm_usuario = nm_usuario_p, 
			dt_atualizacao = clock_timestamp() 
	where 	nr_sequencia = nr_sequencia_w;	
 
	end if;
	end;
end loop;
close C01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
