SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rp_bloq_agenda_altera_status ( ie_status_p bigint, nr_seq_pac_reab_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
nr_seq_pac_reab_bloqueio_w	bigint;
ie_status_agenda_susp_w		varchar(3);
nr_seq_modelo_w			bigint;
nr_seq_ind_w			bigint;

C01 CURSOR FOR
	SELECT distinct	b.nr_sequencia
	from	rp_pac_modelo_agendamento a,
		rp_pac_modelo_agend_item b
	where	coalesce(a.dt_fim_tratamento::text, '') = ''
	and	a.ie_situacao <> 'I'
	and	a.nr_seq_pac_reab   = nr_seq_pac_reab_p
	and	b.nr_seq_modelo_pac = a.nr_sequencia
	order by 1;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	rp_pac_agend_individual
	where	coalesce(dt_fim_tratamento::text, '') = ''
	and	nr_seq_pac_reab = nr_seq_pac_reab_p
	order by 1;

BEGIN

select	max(nr_seq_pac_reab_bloqueio),
	max(ie_status_agenda_susp)
into STRICT	nr_seq_pac_reab_bloqueio_w,
	ie_status_agenda_susp_w
from	rp_parametros
where	cd_estabelecimento = cd_estabelecimento_p;

if (nr_seq_pac_reab_bloqueio_w = ie_status_p) and (ie_status_agenda_susp_w IS NOT NULL AND ie_status_agenda_susp_w::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_seq_modelo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	agenda_consulta
		set	ie_status_agenda 	= ie_status_agenda_susp_w
		where	nr_seq_rp_mod_item 	= nr_seq_modelo_w
		and	ie_status_agenda	= 'N'
		and	trunc(dt_agenda)	>= trunc(clock_timestamp());
		end;
	end loop;
	close C01;

	open C02;
	loop
	fetch C02 into
		nr_seq_ind_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		update	agenda_consulta
		set	ie_status_agenda 	= ie_status_agenda_susp_w
		where	nr_seq_rp_item_ind 	= nr_seq_ind_w
		and	ie_status_agenda	= 'N'
		and	trunc(dt_agenda)	>= trunc(clock_timestamp());
		end;
	end loop;
	close C02;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
