SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rp_consistir_afim_curso (nr_seq_pac_reab_p bigint, nr_seq_curso_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_area_w	bigint;
qt_curso_area_w	bigint;


BEGIN

select	max(nr_seq_area)
into STRICT	nr_seq_area_w
from	tre_curso
where	nr_sequencia = nr_seq_curso_p;

select	count(*)
into STRICT	qt_curso_area_w
from	tre_candidato a,
	tre_agenda b,
	tre_curso c
where	a.nr_seq_agenda 	= b.nr_sequencia
and	b.nr_seq_curso		= c.nr_sequencia
and	nr_pac_reabilitacao 	= nr_seq_pac_reab_p
and	coalesce(b.dt_cancelamento::text, '') = ''
and	clock_timestamp() between dt_inicio and coalesce(dt_termino,clock_timestamp())
and	coalesce(a.dt_desistencia::text, '') = ''
and	c.nr_seq_area		<> nr_seq_area_w;

if (qt_curso_area_w > 0) then
	--O paciente está inscrito em cursos de outra área! Verfique!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(264164);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
