CREATE OR REPLACE PROCEDURE rp_desfaz_fim_tratamento (nr_seq_tratamento_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_pac_reab_final_w		bigint;
nr_seq_pac_reab_w		bigint;
ie_altera_status_final_w	varchar(1);
nr_seq_max_atual_w		bigint;
nr_seq_status_trat_w		bigint;

BEGIN
ie_altera_status_final_w := Obter_param_Usuario(9091, 37, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_altera_status_final_w);


select	max(nr_seq_pac_reav)
into STRICT	nr_seq_pac_reab_w
from	rp_tratamento
where	nr_sequencia = nr_seq_tratamento_p;

select	max(nr_seq_reab_pac_trat)
into STRICT	nr_seq_status_trat_w
from	rp_parametros
where	cd_estabelecimento = cd_estabelecimento_p;


update 	rp_tratamento
set	dt_fim_tratamento  = NULL,
	nr_seq_motivo_fim_trat  = NULL,
	dt_desfeita = clock_timestamp(),
	nr_seq_status = CASE WHEN ie_altera_status_final_w='S' THEN  nr_seq_status_trat_w  ELSE nr_seq_status END
where	nr_sequencia = nr_seq_tratamento_p;


if (ie_altera_status_final_w = 'S') then

	update 	rp_paciente_reabilitacao
	set	nr_seq_status	= nr_seq_status_trat_w
	where	nr_sequencia 	= nr_seq_pac_reab_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
