CREATE OR REPLACE PROCEDURE rp_desfaz_final_modelo_agend ( nr_seq_modelo_p text, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


nr_seq_modelo_pac_w	bigint;
ie_existe_sem_fim_w	varchar(1);

/*
Opções
	DFM - Desfazer Fim do Modelo de Agendamento
	DFI -  Desfazer Fim do Item do Modelo
*/
BEGIN

if (ie_opcao_p = 'DFM') then
	begin
	update	rp_pac_modelo_agendamento
	set	nr_seq_motivo_fim_tratamento  = NULL,
		dt_fim_tratamento  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		dt_desfeita = clock_timestamp()
	where	nr_sequencia = nr_seq_modelo_p;

	update	rp_pac_modelo_agend_item
	set	nr_seq_motivo_fim_tratamento  = NULL,
		dt_fim_tratamento  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_modelo_pac = nr_seq_modelo_p
	and	(dt_fim_tratamento IS NOT NULL AND dt_fim_tratamento::text <> '');

	update	agenda_consulta
	set	ie_status_agenda 	= 'N',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_seq_rp_mod_item in (SELECT	nr_sequencia
					from 	rp_pac_modelo_agend_item
					where 	nr_seq_modelo_pac = nr_seq_modelo_p)
					and	ie_status_agenda not in ('A','B','E','N')
					and	trunc(dt_agenda) > trunc(clock_timestamp());
	end;
elsif (ie_opcao_p = 'DFI') then
	begin
	select	max(nr_seq_modelo_pac)
	into STRICT	nr_seq_modelo_pac_w
	from	rp_pac_modelo_agend_item
	where	nr_sequencia 			= nr_seq_modelo_p;

	update	rp_pac_modelo_agend_item
	set		nr_seq_motivo_fim_tratamento  = NULL,
			dt_fim_tratamento  = NULL,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp(),
			dt_desfeita = clock_timestamp()
	where	nr_sequencia = nr_seq_modelo_p;

	update	agenda_consulta
	set	ie_status_agenda = 'N',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_rp_mod_item 	= nr_seq_modelo_p
	and	ie_status_agenda not in ('A','B','E','N')
	and	trunc(dt_agenda) > trunc(clock_timestamp());

	update	rp_pac_modelo_agendamento
	set	nr_seq_motivo_fim_tratamento  = NULL,
		dt_fim_tratamento  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		dt_desfeita = clock_timestamp()
	where	nr_sequencia = nr_seq_modelo_pac_w;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
