SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rp_gerar_controle_falta (cd_pessoa_fisica_p text, dt_agenda_p timestamp, nr_seq_rp_mod_item_p bigint, nr_seq_rp_item_ind_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_agendamento_w 	bigint;
qt_agendamento_falta_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nr_seq_pac_reab_w	bigint;
dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;


BEGIN

if (nr_seq_rp_mod_item_p IS NOT NULL AND nr_seq_rp_mod_item_p::text <> '') then

	select	max(nr_seq_pac_reab)
	into STRICT	nr_seq_pac_reab_w
	from	rp_pac_modelo_agend_item a,
		rp_pac_modelo_agendamento b
	where	b.nr_sequencia = a.nr_seq_modelo_pac
	and	a.nr_sequencia = nr_seq_rp_mod_item_p;

elsif (nr_seq_rp_item_ind_p IS NOT NULL AND nr_seq_rp_item_ind_p::text <> '') then

	select	max(nr_seq_pac_reab)
	into STRICT	nr_seq_pac_reab_w
	from	rp_pac_agend_individual
	where	nr_sequencia = nr_seq_rp_item_ind_p;

end if;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	pac_reab_falta_agendamento
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	dt_agenda between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 86399/86400;

select	count(*)
into STRICT	qt_agendamento_w
from	agenda_consulta
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	dt_agenda between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 86399/86400
and	((nr_seq_rp_mod_item IS NOT NULL AND nr_seq_rp_mod_item::text <> '') or (nr_seq_rp_item_ind IS NOT NULL AND nr_seq_rp_item_ind::text <> ''))
and	ie_status_agenda <> 'C';

select	count(*)
into STRICT	qt_agendamento_falta_w
from	agenda_consulta
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	dt_agenda between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 86399/86400
and	((nr_seq_rp_mod_item IS NOT NULL AND nr_seq_rp_mod_item::text <> '') or (nr_seq_rp_item_ind IS NOT NULL AND nr_seq_rp_item_ind::text <> ''))
and	ie_status_agenda = 'I';

select	max(dt_periodo_inicial),
	max(dt_periodo_final)
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w
from	rp_licenca
where	nr_seq_pac_reab = nr_seq_pac_reab_w
and	trunc(dt_agenda_p) between dt_periodo_inicial and dt_periodo_final + 86399/86400
and	coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');


if (coalesce(cd_pessoa_fisica_w::text, '') = '') then

	insert into pac_reab_falta_agendamento(nr_sequencia,
					       dt_atualizacao,
					       dt_atualizacao_nrec,
					       nm_usuario,
					       nm_usuario_nrec,
					       cd_pessoa_fisica,
					       dt_agenda,
					       ie_falta)
				    values (nextval('pac_reab_falta_agendamento_seq'),
					       clock_timestamp(),
					       clock_timestamp(),
					       nm_usuario_p,
					       nm_usuario_p,
					       cd_pessoa_fisica_p,
					       trunc(dt_agenda_p,'dd'),
					       'N');

end if;

if (coalesce(dt_periodo_inicial_w::text, '') = '') and (coalesce(dt_periodo_final_w::text, '') = '') and (qt_agendamento_w = qt_agendamento_falta_w) then

	update	pac_reab_falta_agendamento
	set	ie_falta 	 = 'S'
	where	dt_agenda 	 = trunc(dt_agenda_p,'dd')
	and	cd_pessoa_fisica = cd_pessoa_fisica_p;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
