CREATE OR REPLACE PROCEDURE rp_gravar_status_reab (nr_seq_pac_reab_p bigint, ie_status_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_status_ant_w		bigint;
nr_seq_status_ant_w	bigint;

BEGIN

ie_status_ant_w := null;

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_status_ant_w
from	rp_status_pac
where	nr_seq_pac_reab = nr_seq_pac_reab_p;

if (nr_seq_status_ant_w IS NOT NULL AND nr_seq_status_ant_w::text <> '') and (nr_seq_status_ant_w <> 0) then
	select 	nr_seq_status_atual
	into STRICT	ie_status_ant_w
	from	rp_status_pac
	where	nr_sequencia = nr_seq_status_ant_w;
else
	ie_status_ant_w := null;
end if;

insert into rp_status_pac(nr_sequencia,
			   dt_atualizacao,
			   nm_usuario,
			   dt_atualizacao_nrec,
			   nm_usuario_nrec,
			   nr_seq_status_ant,
			   nr_seq_status_atual,
			   dt_alteracao,
			   nr_seq_pac_reab)
values (nextval('rp_status_pac_seq'),
			   clock_timestamp(),
			   nm_usuario_p,
			   clock_timestamp(),
			   nm_usuario_p,
			   ie_status_ant_w,
			   ie_status_p,
			   clock_timestamp(),
			   nr_seq_pac_reab_p);

end;
$body$
