CREATE OR REPLACE PROCEDURE rp_inativar_modelo (nr_seq_modelo_agend_p text, nm_usuario_p text) AS $body$
DECLARE



C01 CURSOR FOR
	SELECT	nr_sequencia
	from	rp_pac_modelo_agend_item
	where   nr_seq_modelo_pac = nr_seq_modelo_agend_p
	order by 1;

nr_seq_rp_mod_item_w	bigint;


BEGIN

update	rp_pac_modelo_agendamento
set	ie_situacao 	= 'I',
	dt_inativacao	= clock_timestamp(),
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_modelo_agend_p;

open C01;
loop
fetch C01 into
	nr_seq_rp_mod_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	agenda_consulta
	set	ie_status_agenda 	= 'C'
	where	nr_seq_rp_mod_item	= nr_seq_rp_mod_item_w
	and	ie_status_agenda not in ('C','F','I','B','E');

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
