SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rp_inserir_curso (nr_seq_curso_p bigint, nr_seq_pac_reab_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


qt_vagas_w	bigint;
qt_candidatos_w	bigint;
nr_seq_agenda_w	bigint;


BEGIN

CALL Rp_consistir_afim_curso(nr_seq_pac_reab_p,nr_seq_curso_p,nm_usuario_p);

select	max(nr_sequencia)
into STRICT	nr_seq_agenda_w
from	tre_agenda
where	nr_seq_curso = nr_seq_curso_p
and	coalesce(dt_liberacao::text, '') = '';


select	max(coalesce(qt_max_pessoas,0))
into STRICT	qt_vagas_w
from	tre_agenda
where	nr_sequencia = nr_seq_agenda_w;


select	count(*)
into STRICT	qt_candidatos_w
from	tre_candidato
where	coalesce(ie_cancelamento,'N') = 'N'
and	nr_seq_agenda = nr_seq_agenda_w;

if (qt_candidatos_w >= qt_vagas_w) then
	--Não é possível gerar treinamento para este curso. Limite de vagas foi excedido!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(264171);
elsif (coalesce(nr_seq_agenda_w::text, '') = '') then
	--Não existe nenhum treinamento disponível para este curso!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(264173);
else
	begin

	insert into tre_candidato( nr_sequencia,
				   dt_atualizacao,
				   nm_usuario,
				   dt_atualizacao_nrec,
				   nm_usuario_nrec,
				   cd_pessoa_fisica,
			 	   nr_seq_agenda,
				   ie_cancelamento,
				   nr_pac_reabilitacao)
			values ( nextval('tre_candidato_seq'),
				   clock_timestamp(),
				   nm_usuario_p,
				   clock_timestamp(),
				   nm_usuario_p,
				   cd_pessoa_fisica_p,
				   nr_seq_agenda_w,
				   'N',
				   nr_seq_pac_reab_p);

	update	tre_pf_lista_espera
	set	ie_status = 'T'
	where	nr_seq_curso 		= nr_seq_curso_p
	and	cd_pessoa_fisica 	= cd_pessoa_fisica_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
