CREATE OR REPLACE PROCEDURE rp_inserir_proc_adic_lista ( nr_seq_lista_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_procedimento_w	bigint;
nr_seq_proc_interno_w	bigint;
ie_origem_proced_w	bigint;
cd_pac_agenda_w		varchar(10);
cd_pac_lista_w		varchar(10);
ds_observacao_w		varchar(255);
C01 CURSOR FOR
	SELECT	cd_procedimento,
		nr_seq_proc_interno,
		ie_origem_proced
	from	rp_lista_espera_proc_adic
	where	nr_seq_lista = nr_seq_lista_p;

BEGIN
if (nr_seq_lista_p IS NOT NULL AND nr_seq_lista_p::text <> '') and (nr_seq_lista_p > 0) and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pac_agenda_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	select	max(cd_pessoa_fisica),
		max(cd_procedimento),
		max(nr_seq_proc_interno),
		max(ie_origem_proced)
	into STRICT	cd_pac_lista_w,
		cd_procedimento_w,
		nr_seq_proc_interno_w,
		ie_origem_proced_w
	from	rp_lista_espera_modelo
	where	nr_sequencia = nr_seq_lista_p;

	update	agenda_consulta
	set	nr_seq_lista_reab = nr_seq_lista_p
	where	nr_sequencia = nr_seq_agenda_p;

	update	rp_lista_espera_modelo
	set	ie_status = 'T'
	where	nr_sequencia = nr_seq_lista_p;

	if (cd_pac_agenda_w = cd_pac_lista_w) then

		if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then
			insert into agenda_consulta_proc(
					nr_sequencia,
					cd_procedimento,
					nr_seq_agenda,
					ie_origem_proced,
					dt_atualizacao,
					nm_usuario,
					ie_executar_proc,
					nr_seq_proc_interno,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
				values (	nextval('agenda_consulta_proc_seq'),
					cd_procedimento_w,
					nr_seq_agenda_p,
					ie_origem_proced_w,
					clock_timestamp(),
					nm_usuario_p,
					'S',
					nr_seq_proc_interno_w,
					clock_timestamp(),
					nm_usuario_p);
		end if;

		open C01;
		loop
		fetch C01 into
			cd_procedimento_w,
			nr_seq_proc_interno_w,
			ie_origem_proced_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			insert into agenda_consulta_proc(
				nr_sequencia,
				cd_procedimento,
				nr_seq_agenda,
				ie_origem_proced,
				dt_atualizacao,
				nm_usuario,
				ie_executar_proc,
				nr_seq_proc_interno,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (	nextval('agenda_consulta_proc_seq'),
				cd_procedimento_w,
				nr_seq_agenda_p,
				ie_origem_proced_w,
				clock_timestamp(),
				nm_usuario_p,
				'S',
				nr_seq_proc_interno_w,
				clock_timestamp(),
				nm_usuario_p);
			end;
		end loop;
		close C01;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
