CREATE OR REPLACE PROCEDURE rp_liberar_bloqueio_agenda ( nr_seq_pac_reab_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE



ie_status_agenda_susp_w	varchar(3);
dt_agenda_w		timestamp;
cd_pessoa_fisica_w	varchar(10);


C01 CURSOR FOR
	SELECT	distinct trunc(dt_agenda)
	from   	agenda_consulta
	where  	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	((nr_seq_rp_mod_item IS NOT NULL AND nr_seq_rp_mod_item::text <> '') or (nr_seq_rp_item_ind IS NOT NULL AND nr_seq_rp_item_ind::text <> ''))
	and	trunc(dt_agenda)	>= trunc(clock_timestamp())
	order by 1;


BEGIN

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	rp_paciente_reabilitacao
where	nr_sequencia = nr_seq_pac_reab_p;


select	max(ie_status_agenda_susp)
into STRICT	ie_status_agenda_susp_w
from	rp_parametros
where	cd_estabelecimento = cd_estabelecimento_p;


open C01;
loop
fetch C01 into
	dt_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_status_agenda_susp_w IS NOT NULL AND ie_status_agenda_susp_w::text <> '') and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

		update	agenda_consulta
		set	ie_status_agenda 	= 'N'
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w
		and	((nr_seq_rp_item_ind IS NOT NULL AND nr_seq_rp_item_ind::text <> '') or (nr_seq_rp_mod_item IS NOT NULL AND nr_seq_rp_mod_item::text <> ''))
		and	ie_status_agenda	= ie_status_agenda_susp_w
		and	trunc(dt_agenda)	>= trunc(clock_timestamp());
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
