CREATE OR REPLACE PROCEDURE rp_update_status_reab (nr_seq_pac_reab_p bigint, ie_status_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_altera_status_trat_w	varchar(1);


BEGIN
ie_altera_status_trat_w := obter_param_usuario(9091, 54, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_altera_status_trat_w);
if (ie_status_p IS NOT NULL AND ie_status_p::text <> '') and (ie_status_p <> 0) then
	update 	rp_paciente_reabilitacao
	set	nr_seq_status = ie_status_p,
		nm_usuario    = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_pac_reab_p;

	if (ie_altera_status_trat_w = 'S') then

		update	rp_tratamento
		set	nr_seq_status	= ie_status_p,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario
		where	nr_seq_pac_reav = nr_seq_pac_reab_p
		and	coalesce(dt_fim_tratamento::text, '') = '';

	end if;

	insert into rp_log_status(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_pac_reab,
		nr_seq_status_atual)
	values (nextval('rp_log_status_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_pac_reab_p,
		ie_status_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
