CREATE OR REPLACE PROCEDURE rxt_ajustar_agenda_equip (nr_seq_equipamento_p bigint, dt_inicio_tratamento_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_dia_semana_w	bigint;
dt_agenda_w		timestamp;

C01 CURSOR FOR
	SELECT	trunc(dt_agenda,'dd')
	from	rxt_agenda
    where	((dt_dia_semana_w = 9 and pkg_date_utils.is_business_day(dt_agenda) = 1) or (dt_dia_semana_w <> 9 and rxt_obter_cod_dia_semana(dt_agenda) <> dt_dia_semana_w))
	and	trunc(dt_agenda,'dd') >= trunc(dt_inicio_tratamento_p,'dd')
	and	ie_tipo_agenda	= 'T'
	and	nr_seq_equipamento	= nr_seq_equipamento_p
	group by	trunc(dt_agenda,'dd');


BEGIN

select	coalesce(max(dt_dia_semana),9)
into STRICT	dt_dia_semana_w
from	rxt_equip_regra_agenda
where	nr_seq_equipamento	= nr_seq_equipamento_p
and	ie_tipo_agenda	= 'T'
and	to_char(hr_inicial, 'hh24:mi:ss') <= to_char(dt_inicio_tratamento_p,'hh24:mi:ss')
and	to_char(hr_final, 'hh24:mi:ss') >= to_char(dt_inicio_tratamento_p,'hh24:mi:ss');

open C01;
loop
fetch C01 into
	dt_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL rxt_gerar_agenda_equip(nr_seq_equipamento_p, dt_agenda_w, 'S', nm_usuario_p);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
