CREATE OR REPLACE PROCEDURE rxt_aplicar_todos_campos ( nr_seq_fase_p bigint, nr_seq_agenda_p bigint, ie_opcao_p text, qt_dose_p bigint, ds_observacao_p text, cd_pessoa_fisica_p text, nr_seq_tratamento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_campo_w	bigint;								
 
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		qt_dose_monitor 
	FROM 	RXT_CAMPO a 
	WHERE 	SUBSTR( rxt_obter_se_campo_aplic(nr_seq_agenda_p,nr_sequencia, 'F'),1,1) <> 'S' 
	and	nr_seq_fase = nr_seq_fase_p;
	
C02 CURSOR FOR 
	SELECT	nr_sequencia 
	FROM 	RXT_BRAQ_CAMPO_APLIC_TRAT a 
	WHERE  SUBSTR( rxt_obter_se_campo_aplic(nr_seq_agenda_p, nr_sequencia, 'B'),1,1) <> 'S' 
	and   exists ( SELECT	1 
		      from	rxt_braq_aplic_trat c 
		      where	a.nr_seq_aplic_trat = c.nr_sequencia 
		      and	c.nr_seq_tratamento = nr_seq_tratamento_p);
	
BEGIN 
if (ie_opcao_p = 'F') then 
	for r_C01 in C01 loop 
		begin 
		CALL RXT_gerar_aplic_campo(	nr_seq_agenda_p, 
					r_C01.nr_sequencia, 
					'A', 
					r_C01.qt_dose_monitor, 
					ds_observacao_p, 
					cd_pessoa_fisica_p, 
					nr_seq_fase_p, 
					ie_opcao_p, 
					nm_usuario_p);
		end;
	end loop;
elsif (ie_opcao_p = 'B') then 
	open C02;
	loop 
	fetch C02 into 
		nr_seq_campo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		CALL RXT_gerar_aplic_campo(	nr_seq_agenda_p, 
					nr_seq_campo_w, 
					'A', 
					qt_dose_p, 
					ds_observacao_p, 
					cd_pessoa_fisica_p, 
					nr_seq_fase_p, 
					ie_opcao_p,  
					nm_usuario_p);	
		end;
	end loop;
	close C02;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
