CREATE OR REPLACE PROCEDURE rxt_atualizar_dt_inicio_trat ( nr_seq_agenda_p bigint, nr_seq_tratamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_agenda_w		timestamp;
nr_seq_tratamento_w	bigint;

BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	/* Obter data da agenda*/

	select	a.dt_agenda
	into STRICT	dt_agenda_w
	from	agenda_consulta a
	where	a.nr_sequencia = nr_seq_agenda_p;

	if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then
		begin
		/* Atualiza a data de in√≠cio de tratamento */

		update	rxt_tratamento
		set	dt_inicio_trat	= dt_agenda_w,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_tratamento_p;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
