SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rxt_desfazer_lib_tratamento (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w bigint;
ie_tipo_trat_w varchar(1);

c01 CURSOR FOR
SELECT 	a.nr_sequencia
from	RXT_CAMPO a,
		rxt_fase_tratamento b
where a.nr_seq_fase = b.nr_sequencia
and a.ie_situacao = 'A'
and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and b.nr_seq_tratamento = nr_sequencia_p;


c02 CURSOR FOR
SELECT a.nr_sequencia
from 	rxt_braq_campo_aplic_trat a,
		rxt_braq_aplic_trat b
where a.nr_seq_aplic_trat = b.nr_sequencia
and a.ie_situacao = 'A'
and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and b.nr_seq_tratamento = nr_sequencia_p;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select Rxt_Obter_Tipo_Trat_Prot(nr_seq_protocolo)
	into STRICT 	ie_tipo_trat_w
	from 	rxt_tratamento
	where 	nr_sequencia =  nr_sequencia_p;

	update  rxt_tratamento
	set 	nm_usuario_desf_lib = nm_usuario_p,
			dt_desf_lib = clock_timestamp()
	WHERE 	nr_sequencia 	= nr_sequencia_p;

	if (ie_tipo_trat_w = 'T') then
			open c01;
				loop
				fetch c01 into
					nr_sequencia_w;
					EXIT WHEN NOT FOUND; /* apply on c01 */
				begin
				update RXT_CAMPO
				set dt_liberacao  = NULL,
					nm_usuario_lib  = NULL
				where nr_sequencia = nr_sequencia_w;

				end;
				end loop;
			close c01;

		elsif (ie_tipo_trat_w = 'B') then
			open c02;
				loop
				fetch c02 into
					nr_sequencia_w;
					EXIT WHEN NOT FOUND; /* apply on c02 */
				begin
				update rxt_braq_campo_aplic_trat
				set dt_liberacao  = NULL,
					nm_usuario_lib  = NULL
				where nr_sequencia = nr_sequencia_w;

				end;
				end loop;
			close c02;
	end if;
end if;

commit;

CALL rxt_limpar_lib_tratamento(nr_sequencia_p, nm_usuario_p );

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE rxt_desfazer_lib_tratamento (nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

