SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rxt_gerar_encaixe ( nr_seq_equipamento_p bigint, dt_agenda_p timestamp, hr_encaixe_p text, qt_duracao_p bigint, cd_pessoa_fisica_p text, nr_seq_tratamento_p bigint, ds_observacao_p text) AS $body$
DECLARE


nr_seq_agenda_w			bigint;
dt_encaixe_w			timestamp;


BEGIN

if (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and
--	(hr_encaixe_p is not null) and
	(qt_duracao_p IS NOT NULL AND qt_duracao_p::text <> '') then

	/* obter sequencia */

	select	nextval('rxt_agenda_seq')
	into STRICT	nr_seq_agenda_w
	;

	/* obter hor√°rio agenda x encaixe */

	if (hr_encaixe_p IS NOT NULL AND hr_encaixe_p::text <> '') then
		dt_encaixe_w := to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' || hr_encaixe_p || ':00','dd/mm/yyyy hh24:mi:ss');
	else
		dt_encaixe_w := dt_agenda_p;
	end if;

	--gerar encaixe
	insert into rxt_agenda(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_equipamento,
				dt_agenda,
				nr_minuto_duracao,
				cd_pessoa_fisica,
				ie_status_agenda,
				ie_classif_agenda,
				nr_seq_tratamento,
				ie_tipo_agenda,
				dt_agendamento,
				ds_observacao)
			values (
				nr_seq_agenda_w,
				clock_timestamp(),
				wheb_usuario_pck.get_nm_usuario,
				clock_timestamp(),
				wheb_usuario_pck.get_nm_usuario,
				nr_seq_equipamento_p,
				dt_encaixe_w,
				qt_duracao_p,
				cd_pessoa_fisica_p,
				'M',
				'E',
				nr_seq_tratamento_p,
				CASE WHEN coalesce(nr_seq_tratamento_p::text, '') = '' THEN 'S'  ELSE 'T' END ,
				clock_timestamp(),
				ds_observacao_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE rxt_gerar_encaixe ( nr_seq_equipamento_p bigint, dt_agenda_p timestamp, hr_encaixe_p text, qt_duracao_p bigint, cd_pessoa_fisica_p text, nr_seq_tratamento_p bigint, ds_observacao_p text) FROM PUBLIC;

