SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rxt_gerar_insercoes_trat_prot (nr_sequencia_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
P - Protocolo
T - Tratamento
*/
nr_seq_aplicador_w	bigint;
nr_insercao_w		integer;
nr_seq_campo_w		bigint;
qt_reg_w			bigint;

C01 CURSOR FOR
	SELECT	nr_seq_campo
	from	rxt_braq_campo_aplicador
	where	nr_seq_aplicador = nr_seq_aplicador_w;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_opcao_p = 'P') then

		select	max(nr_seq_aplicador),
			coalesce(max(nr_insercao),0)
		into STRICT	nr_seq_aplicador_w,
			nr_insercao_w
		from	rxt_braq_aplic_prot
		where	nr_sequencia = nr_sequencia_p;

		delete
		from	rxt_braq_campo_aplic_prot
		where	nr_seq_aplic_prot = nr_sequencia_p
		and		nr_insercao > nr_insercao_w;

		while(nr_insercao_w > 0) loop
			begin

			open C01;
			loop
			fetch C01 into
				nr_seq_campo_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin

				select	count(1)
				into STRICT	qt_reg_w
				from	rxt_braq_campo_aplic_prot
				where	nr_seq_aplic_prot = nr_sequencia_p
				and		nr_seq_campo = nr_seq_campo_w
				and		nr_insercao = nr_insercao_w;

				if (qt_reg_w = 0) then
					insert into rxt_braq_campo_aplic_prot(	nr_sequencia,
										cd_estabelecimento,
										dt_atualizacao,
										nm_usuario,
										dt_atualizacao_nrec,
										nm_usuario_nrec,
										nr_seq_aplic_prot,
										nr_insercao,
										nr_seq_campo,
										ie_situacao)
									values (	nextval('rxt_braq_campo_aplic_prot_seq'),
										cd_estabelecimento_p,
										clock_timestamp(),
										nm_usuario_p,
										clock_timestamp(),
										nm_usuario_p,
										nr_sequencia_p,
										nr_insercao_w,
										nr_seq_campo_w,
										'A');
				end if;

				end;
			end loop;
			close C01;

			nr_insercao_w := nr_insercao_w - 1;
			end;
		end loop;

	elsif (ie_opcao_p = 'T') then

		select	max(nr_seq_aplicador),
			coalesce(max(nr_insercao),0)
		into STRICT	nr_seq_aplicador_w,
			nr_insercao_w
		from	rxt_braq_aplic_trat
		where	nr_sequencia = nr_sequencia_p;

		while(nr_insercao_w > 0) loop
			begin

			open C01;
			loop
			fetch C01 into
				nr_seq_campo_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin

				select	count(1)
				into STRICT	qt_reg_w
				from	rxt_braq_campo_aplic_trat
				where	nr_seq_aplic_trat = nr_sequencia_p
				and		nr_seq_campo = nr_seq_campo_w
				and		nr_insercao = nr_insercao_w;

				if (qt_reg_w = 0) then
					insert into rxt_braq_campo_aplic_trat(	nr_sequencia,
										cd_estabelecimento,
										dt_atualizacao,
										nm_usuario,
										dt_atualizacao_nrec,
										nm_usuario_nrec,
										nr_seq_aplic_trat,
										nr_insercao,
										nr_seq_campo,
										ie_situacao)
									values (	nextval('rxt_braq_campo_aplic_trat_seq'),
										cd_estabelecimento_p,
										clock_timestamp(),
										nm_usuario_p,
										clock_timestamp(),
										nm_usuario_p,
										nr_sequencia_p,
										nr_insercao_w,
										nr_seq_campo_w,
										'A');
				end if;

				end;
			end loop;
			close C01;

			nr_insercao_w := nr_insercao_w - 1;
			end;
		end loop;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE rxt_gerar_insercoes_trat_prot (nr_sequencia_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

