CREATE OR REPLACE PROCEDURE rxt_inserir_orientacao ( nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tratamento_w	bigint;
ds_observacao_w		varchar(255);


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select	nr_seq_tratamento,
		ds_observacao
	into STRICT	nr_seq_tratamento_w,
		ds_observacao_w
	from	rxt_agenda
	where	nr_Sequencia = nr_seq_agenda_p;

	if (nr_seq_tratamento_w IS NOT NULL AND nr_seq_tratamento_w::text <> '') and (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then

		insert into rxt_orientacao(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_tratamento,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_orientacao)
		values (	nextval('rxt_orientacao_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_tratamento_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_observacao_w);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
