SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rxt_liberar_horario_agenda (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_agenda_w	bigint;

qt_w	bigint;

BEGIN

begin
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	/*acesso exclusivo ao registro da tabela*/

	select	1
	into STRICT	qt_w
	from	rxt_agenda
	where	nr_sequencia = nr_seq_agenda_p
	for update;

	select	coalesce(count(*),0)
	into STRICT	qt_agenda_w
	from	rxt_agenda
	where	nr_sequencia		= nr_seq_agenda_p
	and	coalesce(ie_status_agenda,'L')	= 'N'
	and	nm_usuario_acesso		= nm_usuario_p
	and	coalesce(nr_seq_tratamento::text, '') = '';

	if (qt_agenda_w > 0) then
		update	rxt_agenda
		set	ie_status_agenda	= 'L',
			nm_usuario	= nm_usuario_p,
			nm_usuario_acesso	 = NULL
		where	nr_sequencia	= nr_seq_agenda_p;
		commit;
	end if;
end if;
exception
	when others then
	commit;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
