SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rxt_limpar_horarios_livre () AS $body$
DECLARE


qt_registros_w	bigint := 0;
nr_sequencia_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	rxt_agenda a
	where	ie_status_agenda = 'L'
	and		coalesce(nr_seq_tratamento::text, '') = ''
	and		coalesce(cd_pessoa_fisica::text, '') = ''
	and		not exists (SELECT	1
						from	rxt_agenda_fase b
						where	b.nr_seq_agenda_rxt = a.nr_sequencia)
	and		not exists (select	1
						from	procedimento_paciente c
						where	c.nr_seq_agenda_rxt = a.nr_sequencia);


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	delete from rxt_agenda where nr_sequencia = nr_sequencia_w;

	qt_registros_w := qt_registros_w + 1;

	if (qt_registros_w >= 50000) then
		qt_registros_w := 0;
		commit;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE rxt_limpar_horarios_livre () FROM PUBLIC;

