SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rxt_suspender_tratamento (nr_sequencia_p bigint, nr_Seq_motivo_susp_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_agenda_w		bigint;
ie_suspender_agenda_w	varchar(1);
								
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	rxt_agenda 
	where	nr_seq_tratamento = nr_sequencia_p 
	and	ie_status_agenda in ('M','V') 
	order by 1;
								

BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
 
	ie_suspender_agenda_w := obter_valor_param_usuario(3030, 33, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);
 
	update 	RXT_TRATAMENTO 
	set 	dt_suspensao	 	= clock_timestamp(), 
		nm_usuario_susp		= nm_usuario_p, 
		nr_seq_motivo_susp	= nr_Seq_motivo_susp_p, 
		ds_just_suspensao	= ds_justificativa_p 
	where	nr_sequencia		= nr_sequencia_p;
 
	if (ie_suspender_agenda_w = 'S') then 
	 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_agenda_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
		 
			CALL rxt_alterar_status_agenda(nr_seq_agenda_w,'C',nm_usuario_p);
		 
			end;
		end loop;
		close C01;
	 
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
