CREATE OR REPLACE PROCEDURE rxt_vincular_atend_agenda ( nr_seq_agenda_p bigint, nr_seq_tratamento_p bigint, nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ie_param_36_w	varchar(1);
ie_param_45_w	varchar(1);
cd_doenca_w		rxt_tumor.cd_doenca%type;
cd_medico_w		rxt_tumor.cd_medico%type;


BEGIN 
 
ie_param_36_w := obter_param_usuario(3030, 36, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_param_36_w);
ie_param_45_w := obter_param_usuario(3030, 45, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_param_45_w);
 
if (ie_param_36_w = 'S' or ie_param_36_w = 'P') and (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') then 
	 
	update rxt_agenda 
	set	nm_usuario = nm_usuario_p, 
		nr_atendimento = nr_atendimento_p 
	where	nr_seq_tratamento = nr_seq_tratamento_p;
	 
	if (ie_param_36_w = 'P') then 
		update rxt_tumor 
		set nr_atendimento = nr_atendimento_p 
		where coalesce(nr_atendimento::text, '') = '' 
		and nr_sequencia = 
			(SELECT max(nr_seq_tumor) 
			from rxt_tratamento 
			where nr_sequencia = nr_seq_tratamento_p);
	end if;
else	 
	update	rxt_agenda 
	set	nm_usuario = nm_usuario_p, 
		nr_atendimento = nr_atendimento_p 
	where	nr_sequencia = nr_seq_agenda_p;	
end if;
 
 
 
if (ie_param_45_w = 'S') then 
 
	select 	max(cd_doenca), 
		max(cd_medico) 
	into STRICT	cd_doenca_w, 
		cd_medico_w	 
	from  	rxt_tumor 
	where 	cd_pessoa_fisica = obter_pessoa_atendimento(nr_atendimento_p,'C') 
	and	nr_sequencia = (SELECT max(nr_sequencia) 
				from  rxt_tumor 
				where cd_pessoa_fisica = obter_pessoa_atendimento(nr_atendimento_p,'C'));
 
	if (cd_doenca_w IS NOT NULL AND cd_doenca_w::text <> '') then 
		CALL gerar_diagnostico_atend(nr_atendimento_p,cd_doenca_w,cd_medico_w,wheb_usuario_pck.get_nm_usuario);
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
