CREATE OR REPLACE PROCEDURE rxt_vincular_atend_hor ( nr_atendimento_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE


nr_sequencia_w bigint;
dia_atual_w timestamp;
dia_posterior_w timestamp;


BEGIN

dia_atual_w := INICIO_DIA(clock_timestamp());
dia_posterior_w := FIM_DIA(clock_timestamp());

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
  select  nr_sequencia
  into STRICT    nr_sequencia_w
  from    rxt_agenda
  where   dt_agenda between dia_atual_w and dia_posterior_w
  and     coalesce(rxt_obter_pf_tratamento(nr_seq_tratamento,null),cd_pessoa_fisica) = cd_pessoa_fisica_p;

  update  rxt_agenda
  set     nr_atendimento = nr_atendimento_p
  where   nr_sequencia = nr_sequencia_w;

  commit;
end if;

exception
when no_data_found then
  CALL wheb_mensagem_pck.exibir_mensagem_abort(1079652, 'NM_PACIENTE=' || obter_nome_pf(cd_pessoa_fisica_p));
when too_many_rows then
  CALL wheb_mensagem_pck.exibir_mensagem_abort(1079651, 'NM_PACIENTE=' || obter_nome_pf(cd_pessoa_fisica_p));
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
