SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE rx_env_planej_rev_medic ( nr_sequencia_p bigint, ie_tipo_tratamento_p text) AS $body$
DECLARE

    nr_seq_fase_w rxt_campo.nr_seq_fase%type;

BEGIN
if (ie_tipo_tratamento_p = 'AL')then
	update	rxt_fase_tratamento
	set		dt_lib_prim_fisico		=	clock_timestamp(),
			nm_usuario_prim_fisico	= 	wheb_usuario_pck.get_nm_usuario
	where	nr_sequencia        	=	nr_sequencia_p;
	commit;
	
    CALL RXT_GRAVA_LOG_LIBERACAO_FISIC(nr_sequencia_p, null,null,'LF');

else
	update	rxt_braq_aplic_trat
	set		dt_lib_prim_fisico		=	clock_timestamp(),
			nm_usuario_prim_fisico	= 	wheb_usuario_pck.get_nm_usuario
	where	nr_sequencia        	=	nr_sequencia_p;
	commit;

    CALL RXT_GRAVA_LOG_LIBERACAO_FISIC(null, nr_sequencia_p,null,'LF');

end if;
CALL rxt_atualizar_rxt_campo(nr_sequencia_p);
/*
ie_tipo_log
LF	Liberação do primeiro físico
LM	Liberação do médico
LS	Liberação do segundo físico
RM	Reprovação do médico
RS	Reprovação do segundo físico
*/
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
