CREATE OR REPLACE PROCEDURE rx_liberar_hist_tratamento_pac ( nr_sequencia_p bigint, nm_usuario_liberacao_p text) AS $body$
DECLARE

 
dt_liberacao_w		timestamp;
nr_seq_evento_w		bigint;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);

c01 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	from	regra_envio_sms a 
	where	a.ie_evento_disp = 'LHR' 
	and	coalesce(a.ie_situacao,'A') = 'A';


BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
 
	update	rxt_tratamento_hist 
	set	dt_liberacao = clock_timestamp(), 
		nm_usuario_liberacao = nm_usuario_liberacao_p 
	where	nr_sequencia = nr_sequencia_p 
	and	coalesce(dt_liberacao::text, '') = '';
 
	select	max(c.nr_atendimento), 
		max(c.cd_pessoa_fisica) 
	into STRICT	nr_atendimento_w, 
		cd_pessoa_fisica_w 
	from	rxt_tratamento_hist a, 
		rxt_tratamento b, 
		rxt_tumor c 
	where	a.nr_sequencia = nr_sequencia_p 
	and	a.nr_seq_tratamento = b.nr_sequencia 
	and	b.nr_seq_tumor = c.nr_sequencia;
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_w,cd_pessoa_fisica_w,null,nm_usuario_liberacao_p,null,null,null,null,null,null,null,null,null,null,null);
		 
		end;
	end loop;
	close C01;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
