CREATE OR REPLACE PROCEDURE rx_reprov_calc_seg_fisico ( nr_sequencia_p bigint, ds_motivo_p text, ie_tipo_tratamento_p text) AS $body$
BEGIN

if (ie_tipo_tratamento_p = 'AL') then
	update	rxt_fase_tratamento
	set		dt_lib_prim_fisico		 = NULL,
			dt_aprovacao_medico	     = NULL,
			nm_usuario_prim_fisico	 = NULL,
			nm_medico_aprovacao	     = NULL,
            dt_lib_seg_fisico        = NULL,
            nm_usuario_seg_fisico    = NULL
	where	nr_sequencia        	=	nr_sequencia_p;
	commit;
    CALL RXT_GRAVA_LOG_LIBERACAO_FISIC(nr_sequencia_p, null, ds_motivo_p, 'RS');
else
	update	rxt_braq_aplic_trat
	set		dt_lib_prim_fisico		 = NULL,
			dt_aprovacao_medico	     = NULL,
			nm_usuario_prim_fisico	 = NULL,
			nm_medico_aprovacao	     = NULL,
            dt_lib_seg_fisico        = NULL,
            nm_usuario_seg_fisico    = NULL
	where	nr_sequencia        	=	nr_sequencia_p;
	commit;
    CALL RXT_GRAVA_LOG_LIBERACAO_FISIC(null, nr_sequencia_p ,ds_motivo_p, 'RS');
end if;
/*
ie_tipo_log
LF	Liberação do primeiro físico
LM	Liberação do médico
LS	Liberação do segundo físico
RM	Reprovação do médico
RS	Reprovação do segundo físico
*/
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
