CREATE OR REPLACE PROCEDURE sac_encerrar_boletins (cd_estabelecimento_p bigint, qt_dias_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_resp_w		bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia
from	sac_resp_bol_ocor b,
	sac_boletim_ocorrencia a
where	a.nr_sequencia		= b.nr_seq_bo
and	b.ie_status		= 'A'
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.dt_ocorrencia	< clock_timestamp() - qt_dias_p;


BEGIN
open c01;
loop
fetch c01 into
	nr_seq_resp_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	update	sac_resp_bol_ocor
	set	ie_status	= 'S',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_resp_w;

	insert	into	sac_hist_bol_ocor(nr_sequencia,
		nr_seq_resp_bo,
		dt_historico,
		dt_atualizacao,
		nm_usuario,
		ds_historico,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (nextval('sac_hist_bol_ocor_seq'),
		nr_seq_resp_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		wheb_mensagem_pck.get_texto(306078,'qt_dias_p=' || qt_dias_p),
		clock_timestamp(),
		nm_usuario_p);
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
