CREATE OR REPLACE PROCEDURE sac_gerar_comunic_venc_aval ( qt_dias_aviso_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_bo_w			bigint;
dt_resposta_w			timestamp;
dt_aviso_w			timestamp;
dt_avaliacao_w			timestamp;
qt_dias_avaliacao_w		integer;
ie_gerou_w			varchar(1);

c01 CURSOR FOR 
SELECT	a.nr_sequencia 
from	sac_boletim_ocorrencia a 
where	coalesce(a.dt_encerramento::text, '') = '' 
and	coalesce(a.dt_cancelamento::text, '') = '' 
and	coalesce(a.qt_dias_avaliacao_sac,0) > 0 
and	((cd_estabelecimento_p = 0) or (a.cd_estabelecimento = cd_estabelecimento_p)) 
and	exists (SELECT	1 
		from	sac_resp_bol_ocor x 
		where	x.nr_seq_bo = a.nr_sequencia 
		and (trunc(dt_resposta) + a.qt_dias_avaliacao_sac) = (trunc(clock_timestamp()) + qt_dias_aviso_p));


BEGIN 
 
 
open c01;
loop 
fetch c01 into 
	nr_seq_bo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	select	max(dt_resposta) 
	into STRICT	dt_resposta_w 
	from	sac_resp_bol_ocor 
	where	nr_seq_bo = nr_seq_bo_w;
 
	select	qt_dias_avaliacao_sac 
	into STRICT	qt_dias_avaliacao_w 
	from	sac_boletim_ocorrencia 
	where	nr_sequencia = nr_seq_bo_w;
 
	dt_aviso_w	:= trunc(clock_timestamp()) + qt_dias_aviso_p;
	dt_avaliacao_w	:= trunc(dt_resposta_w) + qt_dias_avaliacao_w;
	 
	if (dt_aviso_w = dt_avaliacao_w) then 
		ie_gerou_w := sac_gerar_envio_comunicacao(nr_seq_bo_w, '12', null, null, nm_usuario_p, ie_gerou_w);
	end if;
 
	end;
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
