SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sac_gravar_acesso_bo ( nr_seq_boletim_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		sac_log_acesso_bo.nr_sequencia%type;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	sac_log_acesso_bo
where	nr_seq_boletim = nr_seq_boletim_p
and 	nm_usuario = nm_usuario_p;

if (nr_sequencia_w > 0) then
	begin
	update	sac_log_acesso_bo
	set	dt_ultimo_acesso = clock_timestamp()
	where 	nr_sequencia = nr_sequencia_w;
	end;
else
	begin
	insert into sac_log_acesso_bo(
			nr_sequencia,
			nr_seq_boletim,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_ultimo_acesso)
		values (	nextval('sac_log_acesso_bo_seq'),
			nr_seq_boletim_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp());
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
