CREATE OR REPLACE PROCEDURE sac_inserir_aval_perfil ( cd_estabelecimento_p bigint, nr_seq_aval_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint) AS $body$
DECLARE


cd_funcao_w		integer;
qt_aval_sac_w		integer;
nm_usuario_w		varchar(15);
cd_estabelecimento_w	smallint;
cd_setor_atendimento_w	integer;
cd_pessoa_fisica_w	varchar(10);

--buscar usuarios
c01 CURSOR FOR
SELECT	distinct
	a.nm_usuario
from	usuario a,
	usuario_perfil b,
	usuario_setor c,
	funcao_perfil d
where	a.nm_usuario = b.nm_usuario
and	a.nm_usuario = c.nm_usuario
and	d.cd_funcao = 2000
and	d.cd_perfil = b.cd_perfil
and	((b.cd_perfil = cd_perfil_p) or (cd_perfil_p = 0))
and	(((c.cd_setor_atendimento = cd_setor_atendimento_p) or (cd_setor_atendimento_p = 0)) or
	((a.cd_setor_atendimento = cd_setor_atendimento_p) or (cd_setor_atendimento_p = 0)))
and	((a.cd_estabelecimento = cd_estabelecimento_p) or (cd_estabelecimento_p = 0));

BEGIN
--verifica se a avaliação é da função boletim de ocorrência e está ativo.
select	cd_funcao
into STRICT	cd_funcao_w
from	med_tipo_avaliacao
where	ie_situacao = 'A'
and	nr_sequencia = nr_seq_aval_p;

if (cd_estabelecimento_p = 0) then
	cd_estabelecimento_w := null;
else	cd_estabelecimento_w := cd_estabelecimento_p;
end if;
if (cd_setor_atendimento_p = 0) then
	cd_setor_atendimento_w := null;
else	cd_setor_atendimento_w := cd_setor_atendimento_p;
end if;

if (cd_funcao_w = 2000) then
	begin
	open c01;
	loop
	fetch c01 into
		nm_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	cd_pessoa_fisica
		into STRICT	cd_pessoa_fisica_w
		from	usuario
		where	nm_usuario = nm_usuario_w;

		select	count(*)
		into STRICT	qt_aval_sac_w
		from	sac_pesquisa
		where	nr_seq_tipo_avaliacao = nr_seq_aval_p
		and	cd_pessoa_fisica = cd_pessoa_fisica_w;

		if (qt_aval_sac_w = 0) then
			begin
			insert 	into 	sac_pesquisa(
					nr_sequencia,
					dt_avaliacao,
					dt_atualizacao,
					nm_usuario,
					cd_pessoa_fisica,
					nr_seq_tipo_avaliacao,
					ds_observacao,
					cd_estabelecimento,
					dt_liberacao,
					cd_setor_atendimento,
					nr_seq_apres,
					nr_seq_ciclo,
					dt_cancelamento,
					nm_usuario_cancelamento,
					cd_perfil)
				values (nextval('sac_pesquisa_seq'),
					clock_timestamp(),
					clock_timestamp(),
					nm_usuario_w,
					cd_pessoa_fisica_w,
					nr_seq_aval_p,
					null,
					cd_estabelecimento_w,
					null,
					cd_setor_atendimento_w,
					null,
					null,
					null,
					null,
					cd_perfil_p);
			commit;
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
