CREATE OR REPLACE PROCEDURE sac_inserir_pesquisa_tipo ( nr_seq_tipo_avaliacao_p bigint, cd_estab_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_medico_w	bigint;


c01 CURSOR FOR
SELECT 		cd_medico
from		med_medico_avaliacao
where		NR_SEQ_TIPO_AVAL = nr_seq_tipo_avaliacao_p
and (select   count(*)
		from     sac_pesquisa
                where    nr_seq_tipo_avaliacao = NR_SEQ_TIPO_AVAL
                and      cd_pessoa_fisica = cd_medico) = 0;




BEGIN
open c01;
loop
fetch c01 into
		cd_medico_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */

insert into	sac_pesquisa(
		nr_sequencia,
		dt_avaliacao,
		dt_atualizacao,
		nm_usuario,
		cd_pessoa_fisica,
		nr_seq_tipo_avaliacao,
		ds_observacao,
		cd_estabelecimento,
		dt_liberacao,
		cd_setor_atendimento,
		nr_seq_apres,
		nr_seq_ciclo,
		dt_cancelamento,
		nm_usuario_cancelamento)
	values (nextval('sac_pesquisa_seq'),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		cd_medico_w,
		nr_seq_tipo_avaliacao_p,
		null,
		cd_estab_p,
		null,
		null,
		null,
		null,
		null,
		null);

commit;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
