CREATE OR REPLACE PROCEDURE sac_liberar_cancelar_obs ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p > 0) then
	begin
	if (ie_opcao_p = 'C') then
		update	sac_bol_ocor_obs
		set	dt_cancelamento	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p = 'L') then
		update	sac_bol_ocor_obs
		set	dt_liberacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
	end if;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
