CREATE OR REPLACE PROCEDURE sac_vincular_bo ( nr_seq_bo_p bigint, nr_seq_bo_vinc_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* ie_opca_p
V - Vincular
D - Desvincular
*/
BEGIN
if (ie_opcao_p = 'V') then
	begin
	insert	into sac_boletim_vinculado(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_bo,
						nr_seq_bo_vinc)
					values (	nextval('sac_boletim_vinculado_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_bo_p,
						nr_seq_bo_vinc_p);
	end;
elsif (ie_opcao_p = 'D') then
	begin
	delete
	from	sac_boletim_vinculado
	where	nr_seq_bo_vinc = nr_seq_bo_vinc_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
