CREATE OR REPLACE PROCEDURE salvar_forma_prog_diaria ( cd_funcao_p ultima_forma_prog_diaria.cd_funcao%type, ie_forma_apresentacao_p ultima_forma_prog_diaria.ie_forma_apresentacao%type default 'S', nm_usuario_p ultima_forma_prog_diaria.nm_usuario%type  DEFAULT NULL) AS $body$
DECLARE


nr_sequencia_w            ultima_forma_prog_diaria.nr_sequencia%type;

/*
    If IE_FORMA_APRESENTACAO is "S = Samll"
    If IE_FORMA_APRESENTACAO is "M = Low"
    If IE_FORMA_APRESENTACAO is "L = Large"
*/
BEGIN

    select max(nr_sequencia)
    into STRICT nr_sequencia_w
    from ultima_forma_prog_diaria
    where cd_funcao = cd_funcao_p
    and nm_usuario = nm_usuario_p;

    if (coalesce(nr_sequencia_w::text, '') = '') then

        select nextval('ultima_forma_prog_diaria_seq')
        into STRICT nr_sequencia_w
;

        insert into ultima_forma_prog_diaria(
        nr_sequencia,
        cd_funcao,
        nm_usuario_nrec,
        nm_usuario,
        ie_forma_apresentacao,
        dt_atualizacao_nrec,
        dt_atualizacao
        ) 
        values (
        nr_sequencia_w, 
        cd_funcao_p,
        nm_usuario_p,
        nm_usuario_p,
        ie_forma_apresentacao_p,
        clock_timestamp(),
        clock_timestamp()
        );
    else
        update ultima_forma_prog_diaria
        set ie_forma_apresentacao = ie_forma_apresentacao_p,
        dt_atualizacao = clock_timestamp()
        where nr_sequencia = nr_sequencia_w
        and cd_funcao = cd_funcao_p;
    end if;

    commit;

end;
