CREATE OR REPLACE PROCEDURE salvar_pessoa_juridica_estab (cd_cnpj_p text, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE


qt_estab_existe_w	bigint;


BEGIN
--Procedure executada apenas para o parâmetro 12 da função Pessoa Jurídica em HTML5
select count(*)
into STRICT qt_estab_existe_w
from pessoa_juridica_estab
where cd_estabelecimento = coalesce(cd_estabelecimento_p,'0')
and cd_cgc = coalesce(cd_cnpj_p, '0');

if (qt_estab_existe_w = 0) then
	insert into pessoa_juridica_estab(
		nr_sequencia,
		cd_cgc,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_conta_dif_nf,
		ie_rateio_adiant,
		ie_retem_iss,
		ie_exige_anexo_nf,
		ie_exige_ordem_compra,
		ie_consiste_nr_serie_nf,
		ie_rec_email_comercial
		)
	values (nextval('pessoa_juridica_estab_seq'),
		cd_cnpj_p,
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		'N',
		'P',
		'N',
		'N',
		'N',
		'S',
		'N'
	);
end if;

commit;

end;
$body$
