	values (nextval('prescr_proc_auditoria_seq'), nr_prescricao_p, nr_seq_prescricao_p, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, ie_status_p, ds_laudo_original_p);

