CREATE OR REPLACE PROCEDURE same_armaz_protocolo_doc ( nr_seq_prot_doc_p bigint, nr_seq_local_p bigint, nr_seq_caixa_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_atendimento_w		bigint;
nr_seq_same_w			bigint;	
ie_permite_util_pront_w		varchar(1);	
qt_prontuarios_w		bigint;
ie_tipo_protocolo_w		varchar(3);
	
C01 CURSOR FOR
	SELECT	distinct nr_documento,
		b.ie_tipo_protocolo
	from	protocolo_doc_item a,
		protocolo_documento b
	where	b.nr_sequencia = nr_seq_prot_doc_p
	and	a.nr_sequencia = b.nr_sequencia
	and	b.ie_tipo_protocolo in (2,5)
	and coalesce(a.dt_retirada::text, '') = '';
	
C02 CURSOR FOR
	SELECT	c.nr_sequencia
	from	same_prontuario c
	where	c.nr_atendimento = nr_atendimento_w
	and	((ie_tipo_protocolo_w <> 5) or (coalesce(c.nr_interno_conta::text, '') = '')
		or	('S' = (SELECT	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
				from	protocolo_doc_item a
				where	a.nr_sequencia = nr_seq_prot_doc_p
				and	((coalesce(a.nr_seq_interno::text, '') = '') or (c.nr_interno_conta = a.nr_seq_interno))
					))
					)
	and	coalesce(nr_seq_caixa::text, '') = '';	

BEGIN

select	max(coalesce(ie_permite_util_pront,'N'))
into STRICT	ie_permite_util_pront_w
from	same_local
where	nr_sequencia = nr_seq_local_p;

if (ie_permite_util_pront_w = 'S') then
	select	count(*)
	into STRICT	qt_prontuarios_w
	from	same_prontuario
	where	nr_seq_caixa = nr_seq_caixa_p;
	
	if (qt_prontuarios_w > 1) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(215117);
	end if;

end if;

open C01;
loop
fetch C01 into	
	nr_atendimento_w,
	ie_tipo_protocolo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	open C02;
	loop
	fetch C02 into	
		nr_seq_same_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin		
		CALL Consistir_prontuario_local(nr_seq_same_w,nr_seq_local_p,cd_estabelecimento_p,nm_usuario_p);
		CALL Gestao_Prontuario_Same(nr_seq_same_w,nm_usuario_p,'','',nr_seq_local_p,nr_seq_caixa_p,'','',13,'','','',cd_estabelecimento_p);				
		end;
	end loop;
	close C02;
	
	
	end;	
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
