CREATE OR REPLACE PROCEDURE same_atualizar_tipo_atend ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	update	same_prontuario
	set	ie_tipo_atend_prontuario = 1,
		dt_atualizacao 	= clock_timestamp(),
		nm_usuario 	= nm_usuario_p
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and (ie_tipo_atend_prontuario <> 1
	or	coalesce(ie_tipo_atend_prontuario::text, '') = '');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
