CREATE OR REPLACE PROCEDURE same_desfazer_devolucao_lote ( nr_seq_solicitacao_p bigint, ie_atualizar_pront_p text, nm_usuario_p text) AS $body$
DECLARE

nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nr_seq_lote_w		bigint;
ds_parametro_w		varchar(10) := obter_valor_param_usuario(941,103,obter_perfil_ativo,nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento);

BEGIN
if (nr_seq_solicitacao_p IS NOT NULL AND nr_seq_solicitacao_p::text <> '') then

	select	max(nr_atendimento),
		max(cd_pessoa_fisica),
		max(nr_seq_lote)
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w,
		nr_seq_lote_w
	from	same_solic_pront
	where	nr_sequencia = nr_seq_solicitacao_p;

	if (ie_atualizar_pront_p = 'S') then
		update	same_solic_pront_lote
		set	ie_status	= 'V'
		where	nr_sequencia	= nr_seq_lote_w;
	end if;

	update	same_solic_pront
	set	dt_receb_devolucao	 = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		nm_usuario_receb_dev 	 = NULL,
		ie_status		= 'V'
	where	nr_sequencia		= nr_seq_solicitacao_p;

  --458298 = 'Solicitacao: '||nr_seq_solicitacao_p||' alterado para Em Devolução.
	CALL gravar_log_tasy(45878,WHEB_MENSAGEM_PCK.get_texto(458298, 'nr_seq_solicitacao='||(nr_seq_solicitacao_p)),nm_usuario_p);

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
